import './App.css';
import { Button, Box, Card, CardContent, Container, Typography } from '@material-ui/core/';
import { makeStyles } from '@material-ui/styles'
import { useMediaQuery } from '@material-ui/core';
import { BrowserRouter as Router, Route, Routes} from 'react-router-dom'; 
import { Contact } from './contact.js'
import { Hem } from './hem.js'
import { PostList } from './postlist.js'
import { SinglePost } from './singlePost.js'
import background_image from "../src/images/dark-background_1.jpg";



function App() {

const isActive = useMediaQuery("max-width: 1000px");

const drawerWidth="240px";
const drawerHeight="250px";

const useStyles = makeStyles({
  container: {
  },
  mainDiv: {
    textAlign: "center",
    backgroundImage: `url(${background_image})`,
    // backgroundColor: "#f0f0f0",
    minHeight: "100vh",
    minWidth: "100vh"
  },
  root: {
    display:"inline-flex",
    flexDirection: "row",
    justifyContent: 'space-between'
  },
  boxLeft: {
    marginTop: "20px",
  },
  cardMain: {
    width: "700px",
    marginLeft: "20px",
    marginRight: "20px",
    padding: "0",
    minHeight: "70vh"
  },
  cardProfile: {
    width: drawerWidth,
    height: '200px',
    marginTop:"0px" 
  },
  cardLeft: {
    width: drawerWidth,
    marginTop:drawerHeight
  },
  cardRight: {
    width: drawerWidth,
    marginTop: drawerHeight,
    maxHeight: "400px"
  },
  cardContentMain : {
    margin: '0',
    padding: '0'
  },
  buttonCard : {
    display: "inline-flex",
    flexDirection: "column",
    justifyContent: 'space-around',
    padding: "10px",
    margin: "2px",
    size: "large",
    width: "150px",
    backgroundColor: "rgb(51,51,51, 0.2)"
  },
  image : {
    marginTop: "10px",
    maxWidth : "100%"
  },
  imgProfile : {
    marginTop: "10px",
    maxWidth : "60%"
      },
  imgHeader : {
    width : "700px",
    height : "200px",
    objectFit: "cover",

      },
  headerText : {
    paddingTop: "20px",
    fontFamily: "monospace",
    color: "rgb(0,0,66)"
  } 
})

const classes = useStyles();
return (
    <Router>
      <div className={classes.mainDiv}>
      <Container className={classes.container}>

      <Box className={classes.root}>

      <Box className={classes.boxLeft}>
        {/* <Card className={classes.cardProfile}>
          <CardContent>
            <Typography>Fredrik Fischer</Typography>
            <img className={classes.imgProfile} src="../images/profilbild_test.jpg"></img>
          </CardContent>
        </Card> */}




        <Card className={classes.cardLeft}>
          <CardContent>
            <Button onClick={event =>  window.location.href='/'} className={classes.buttonCard}>Hem</Button>
            <Button onClick={event =>  window.location.href='/musik/'} className={classes.buttonCard}>Musik</Button>
            <Button onClick={event =>  window.location.href='/posts/'} className={classes.buttonCard}>Inlägg</Button>
            <Button onClick={event =>  window.location.href='/contact'} className={classes.buttonCard}>Kontakt</Button>
          </CardContent>
        </Card>
      </Box>

      <Card className={classes.cardMain}>
        <CardContent className={classes.cardContentMain}> 
        <img className={classes.imgHeader} src="../images/Header-website.png"></img>

        {/* {<PostList/>} */}


          <Routes>
            <Route exact path="/" element={<Hem/>}/>
            {/* <Route path="/:topic" element={<Topic/>}/> */}
            <Route path="/posts/:id" element={<SinglePost />}/>
            <Route path="/posts/" element={<PostList/>}/>
            <Route path="/contact" element={<Contact/>}/>
          </Routes>
        </CardContent>
      </Card>



     <Card className={classes.cardRight}>
      <CardContent>
      <Typography>Sidan är under konstruktion</Typography>

      </CardContent>
     </Card>

      </Box>
      </Container>


    </div>
    </Router>
  );
}

export default App;
