import React from 'react';
import postlist from './posts.json';
import Markdown from 'markdown-to-jsx';
import { makeStyles } from '@material-ui/styles';
import { Button, Card } from '@material-ui/core';
import { AccessAlarm, ArrowBack } from '@material-ui/icons';
import { CardContent } from '@mui/material';

const SinglePost = (props) => {



    const useStyles = makeStyles({
        container: {
        },
        buttonCard : {
          display: "inline-flex",
          flexDirection: "column",
          justifyContent: 'space',
          padding: "10px",
          margin: "2px",
          size: "small",
          borderRadius: "20px",
          backgroundColor: "#6495ed"
          
        },
        buttonCard : {
          display: "inline-flex",
          flexDirection: "column",
          justifyContent: 'space',
          padding: "10px",
          margin: "2px",
          size: "small",
          borderRadius: "20px",
          backgroundColor: "#6495ed"
          
        }
      })
      
      const classes = useStyles();

    const fetchedPost = {}
    postlist.forEach((post, i) => {
        const url = window.location.pathname
        const urlsplit = url.split("/");
        const validId = parseInt(urlsplit[2])
        if (validId === post.id){
            fetchedPost.title = post.title ? post.title : "No title given"
            fetchedPost.date = post.date ? post.date : "No date given"
            fetchedPost.author = post.author ? post.author : "No author given"
            fetchedPost.description = post.description ? post.description : "No description given"
            fetchedPost.content = post.content ? post.content : "No content given"
        } 
    })

    console.log(fetchedPost)


    return(
        <div>
            <Card>
                <CardContent className={classes.topBar}>
                    <Button onClick={event =>  window.location.href='/'} className={classes.buttonCard}><ArrowBack/></Button>
                    </CardContent>
            </Card>
             

            <Markdown>
            {fetchedPost.content}
            </Markdown>
        </div>
    )
}

export {SinglePost}
