import { React, useEffect, useState, useStyles } from 'react'
import { Box, Card, CardContent, Typography } from '@material-ui/core/';
import { makeStyles } from '@material-ui/styles'
import './App.js'



const BildRandom = () => {

  const useStyles = makeStyles({
    container: {
    },
    cardPost: {
      backgroundColor: "#ffffff",
      marginBottom: "5px",
      borderRadius: '2px'
    },
    headerText: {
      fontWeight: "bolder"
    },
    image : {
        maxWidth : "100%"
    }
  })

  const classes = useStyles();

    const [apiResponse, setAPIResponse] = useState('Something Went Wrong...');

    
    useEffect(() => {
        fetch('http://localhost:3001/bilder')
        .then(res => res.json())
        .then(res=>setAPIResponse(res));       
    },[])


    var postId = []
    var postIdReversed = []
    var header = []
    var headerReversed = []
    var text = []
    var textReversed = []
    var haspicture = []
    var haspictureReversed = []


    //header

    for(var i=0; i<apiResponse.length; i++){
      header.push(apiResponse[i].img_id)
    }

    for(var i=apiResponse.length-1; i>=0; i--){
        headerReversed.push(apiResponse[i].img_id)
      }

    // text

    for(var i=0; i<apiResponse.length; i++){
      text.push(apiResponse[i].img_name)
    }

    for(var i=apiResponse.length-1; i>=0; i--){
        textReversed.push(apiResponse[i].img_name)
      }

    // image

    for(var i=0; i<apiResponse.length; i++){
      haspicture.push(apiResponse[i].img_path)
    }

    for(var i=apiResponse.length-1; i>=0; i--){
        haspictureReversed.push(apiResponse[i].img_path)
      }

    //   var randomNum = Math.floor(Math.random() * haspicture.length);
     


    return (   
    <Box>
              <img className={classes.image} src="./featured_images/picture2.jpg" alt="" maxidth="500"></img> 
              {/* <iframe width='560' height='420' src='https://www.youtube.com/embed/5qap5aO4i9A' title='YouTube video player' frameborder='0' allow='accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture' allowfullscreen></iframe> */}
              <Typography className={classes.headerText} variant="h7">
              {text[2]}
            </Typography>
    </Box>
  );  
  };
  
  export { BildRandom };
