import { React, useEffect, useState, useStyles } from 'react'
import { Box, Card, CardContent, Typography } from '@material-ui/core/';
import { makeStyles } from '@material-ui/styles'
import './App.js'
import { Bilder } from './Bilder.js'
import  ReactMarkdown  from 'react-markdown'


const Topic = () => {

  const useStyles = makeStyles({
    container: {
    },
    cardPost: {
      backgroundColor: "#ffffff",
      marginBottom: "5px",
      borderRadius: '2px'
    },
    headerText: {
      fontWeight: "bolder"
    },
    headerLink: {
      textDecoration: "none",
      color: "black"
    },
  })

  const classes = useStyles();

    const [apiResponse, setAPIResponse] = useState('Something Went Wrong...');
    const [apiImageResponse, setAPIImageResponse] = useState('Something Went Wrong...');

    
    useEffect(() => {
        fetch(window.location.protocol + '//' + window.location.hostname + ':3001' + window.location.pathname)
        .then(res => res.json())
        .then(res=>setAPIResponse(res));       
    },[])

    var postId = []
    var postIdReversed = []
    var header = []
    var headerReversed = []
    var text = []
    var textReversed = []
    var postTopic = []
    var postTopicReversed = []
    var hasPicture = []
    var hasPictureReversed = []
    var hasVideo = []
    var hasVideoReversed = []


    for(var i=0; i<apiResponse.length; i++){
      postId.push(apiResponse[i].post_id)
    }

    for(i=apiResponse.length-1; i>=0; i--){
      postIdReversed.push(apiResponse[i].post_id)
    }

    for(i=0; i<apiResponse.length; i++){
      header.push(apiResponse[i].post_header)
    }

    for(i=apiResponse.length-1; i>=0; i--){
      headerReversed.push(apiResponse[i].post_header)
    }

    for(i=0; i<apiResponse.length; i++){
      text.push(apiResponse[i].post_text_short)
    }

    for(i=apiResponse.length-1; i>=0; i--){
      textReversed.push(apiResponse[i].post_text_short)
    }

    for(i=0; i<apiResponse.length; i++){
      postTopic.push(apiResponse[i].post_topic)
    }

    for(i=apiResponse.length-1; i>=0; i--){
      postTopicReversed.push(apiResponse[i].post_topic)
    }

    for(i=0; i<apiResponse.length; i++){
      hasPicture.push(apiResponse[i].post_img)
    }

    for(i=apiResponse.length-1; i>=0; i--){
        hasPictureReversed.push(apiResponse[i].post_img)
      } 
      
      for(i=0; i<apiResponse.length; i++){
      hasVideo.push(apiResponse[i].post_video)
    }

    for(i=apiResponse.length-1; i>=0; i--){
        hasVideoReversed.push(apiResponse[i].post_video)
      }    

      
    
    return (   
    <Box>
      {postIdReversed.map((index, num) => {
        return  (
        <Card className={classes.cardPost} key={index}>
          <CardContent>
            <Typography className={classes.headerText} variant="h5">

              {<a className={classes.headerLink} href={"/post/" + postIdReversed[num]}>{headerReversed[num]}</a>}
            </Typography>
            <br></br>
            <Typography>
            {/* {textReversed[num]} */}
            <div dangerouslySetInnerHTML={{ __html: textReversed[num] }}></div>
              {hasPicture[num] ? <img src={"../featured_images/" + hasPicture[num]} alt="" max width="500"></img> : null}
              {hasVideo[num] ? <iframe width="560" height="315" src={hasVideo[num]} title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe> : null}
              </Typography>
          </CardContent>
        </Card>) 
      })}
    </Box>
  );  
  };
  
  export {Topic};
